package programme;
//WAP to swap two variable using new variable.

public class swappingTwoNumbersUsingVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		int b=4;
		
		int c=a;
		a=b;
		b=c;
		
		System.out.println(a);
		System.out.println(b);
		

	}

}
